import osimport csv# Path to collect data from the Resources folderfilepath = os.path.join('Resources', 'budget_data.csv')# Open CSV file and skip headerwith open(filepath) as csvfile:    csvreader = csv.reader(csvfile)    header = next(csvreader)    # Lists to hold data        total_months = []    profit = []    change_in_profit = []    # Add data to the lists    for row in csvreader:        total_months.append(row[0])        profit.append(int(row[1]))    for i in range(len(profit)-1):        change_in_profit.append(profit[i+1]-profit[i])# Evaluate data    increase = max(change_in_profit)    decrease = min(change_in_profit)    month_increase = change_in_profit.index(max(change_in_profit))+1    month_decrease = change_in_profit.index(min(change_in_profit))+1# Print analysis in terminalprint("Financial Analysis")print("----------------------------")print(f"Total Months: {len(total_months)}")print(f"Total: ${sum(profit)}")print(f"Average Change: {round(sum(change_in_profit)/len(change_in_profit),2)}")print(f"Greatest Increase in Profits: {total_months[month_increase]} (${(str(increase))})")print(f"Greatest Decrease in Profits: {total_months[month_decrease]} (${(str(decrease))})")# Write analysis to text fileoutput = os.path.join('Analysis', 'FinancialAnalysis.txt')with open(output, "w") as pybank:    pybank.write("Financial Analysis\n")    pybank.write("----------------------------\n")    pybank.write(f"Total Months: {len(total_months)}\n")    pybank.write(f"Total: ${sum(profit)}\n")    pybank.write(f"Average Change: {round(sum(change_in_profit)/len(change_in_profit),2)}\n")    pybank.write(f"Greatest Increase in Profits: {total_months[month_increase]} (${(str(increase))})\n")    pybank.write(f"Greatest Decrease in Profits: {total_months[month_decrease]} (${(str(decrease))})")        