import osimport csv# Path to collect data from the Resources folderfilepath = os.path.join('Resources', 'election_data.csv')# Open CSV file and skip headerwith open(filepath, newline = "") as csvfile:    csvreader = csv.reader(csvfile)    header = next(csvreader)    # Lists to hold data    total_votes = 0candidates = []number_of_votes = []# Calculate total number of votesfor row in csvreader:    total_votes += 1 # Determine candidates and number of votes for eachif row[2] not in candidates:    candidates.append(row[2])    index = candidates.index(row[2])    number_of_votes.append(1)else:    index = candidates.index(row[2])    number_of_votes[index] += 1