import osimport csv# Path to collect data from the Resources folderfilepath = os.path.join('Resources', 'election_data.csv')# Lists to hold data    total_votes = 0candidates = []number_of_votes = []percentage_of_votes = []# Open CSV file and skip headerwith open(filepath, newline = "") as csvfile:    csvreader = csv.reader(csvfile)    header = next(csvreader)# Calculate total number of votes    for row in csvreader:        total_votes += 1 # Determine candidates and number of votes for each        if row[2] not in candidates:            candidates.append(row[2])            index = candidates.index(row[2])            number_of_votes.append(1)        else:            index = candidates.index(row[2])            number_of_votes[index] += 1    # Calculate percentage of votes    for votes in number_of_votes:        percentage = (votes/total_votes) * 100        percentage = round(percentage)        percentage = "%.3f%%" % percentage        percentage_of_votes.append(percentage)    # Determine the winning candidate    winner = max(number_of_votes)    index = number_of_votes.index(winner)    winning_candidate = candidates[index]# Print election results in terminalprint("Election Results")print("-------------------------")print(f"Total Votes: {str(total_votes)}")print("-------------------------")for i in range(len(candidates)):    print(f"{candidates[i]}: {str(percentage_of_votes[i])} ({str(number_of_votes[i])})")print("-------------------------")print(f"Winner: {winning_candidate}")print("-------------------------")# Write results to text fileoutput = os.path.join('Analysis', 'ElectionResults.txt')with open(output, "w") as pypoll:    pypoll.write("Election Results\n")    pypoll.write("-------------------------\n")    pypoll.write(f"Total Votes: {str(total_votes)}\n")    pypoll.write("-------------------------\n")    for i in range(len(candidates)):        pypoll.write(f"{candidates[i]}: {str(percentage_of_votes[i])} ({str(number_of_votes[i])})\n")    pypoll.write("-------------------------\n")    pypoll.write(f"Winner: {winning_candidate}\n")    pypoll.write("-------------------------")